body {
    background: rgb(39,35,35);
    background: linear-gradient(4deg, rgba(39,35,35,1) 0%, rgba(204,150,34,1) 42%, rgba(255,185,34,1) 50%, rgba(141,106,35,1) 74%);
}
main {
    padding: 50px 0;
    display: flex;
    justify-content: start;
    align-items: center;
    flex-direction: column;
    gap: 100px;
}
button {
    pointer-events: auto;
}
.main-title {
    color: white;
    font-family: "Signika Negative", serif;
    font-optical-sizing: auto;
    font-style: normal;
    font-weight: 800;
    font-size: 80px;
    padding: 0;
    margin: 0;
}
/* form */

.students-form {
    width: 400px;
    display: flex;
    flex-direction: column;
    gap: 10px;
    padding: 40px;
    border: 2px solid #000000;
    border-radius: 20px;
    background: rgba(39,35,35,0.5);
    transition: all 0.4s ease-in-out;
}
.students-form.active {
    background: rgb(31,121,9);
    background: linear-gradient(164deg, rgba(31,121,9,0.8253895308123249) 30%, rgba(255,241,0,1) 100%);
}
.students-form__label {
    color: white;
    font-family: "Signika Negative", serif;
    font-optical-sizing: auto;
    font-style: normal;
}
.students-form__input, .students-form__select, .students-form__textarea {
    padding: 8px;
    border: 1px solid #ccc;
    border-radius: 20px;
    font-size: 16px;
}
.students-form__button--add {
    display: block;
    margin-top: 40px;
    background-color: #ff8001;
    color: white;
    padding: 10px;
    border: none;
    border-radius: 20px;
    font-size: 16px;
    cursor: pointer;
    transition: background-color 0.3s ease;
}
.students-form__button--add:hover {
    opacity: 0.8;
    transform: scale(1.1);
    transition: all 0.2s ease-in-out;
}
.students-form__button--add.disable {
    display: none;
}
.students-form__button--save {
    display: none;
    margin-top: 40px;
    background-color: #ff8001;
    color: white;
    padding: 10px;
    border: none;
    border-radius: 20px;
    font-size: 16px;
    cursor: pointer;
    transition: background-color 0.3s ease;
}
.students-form__button--save:hover {
    opacity: 0.8;
    transform: scale(1.1);
    transition: all 0.2s ease-in-out;
}
.students-form__button--save.active {
    display: block;
}

/* table */

.students-table {
    position: relative;
    min-width: 1100px;
    height: auto;
    display: flex;
    justify-content: start;
    align-items: center;
    flex-direction: column;
    padding: 20px;
    gap: 40px;
    border-radius: 20px;
    background-color: aliceblue;
}
.students-table::before {
    content: "Your students will be here ...";
    width: auto;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    font-family: "Atkinson Hyperlegible Mono", serif;
    font-optical-sizing: auto;
    font-weight: 300;
    font-style: normal;
    color: #828282;
}
.students-table.disable::before { 
    display: none;
}
.students-table__header {
    width: 100%;
}
.students-table__header-tr {  
    width: 100%;
    display: flex;
    justify-content: space-between;
    align-items: center;
}
.students-table__header-th {
    width: 14.28%; 
    display: flex;
    justify-content: center;
    align-items: center;
    font-family: "Atkinson Hyperlegible Mono", serif;
    font-optical-sizing: auto;
    font-weight: 700;
    font-style: normal;
    font-size: 15px;
}
.students-table__download-btn {
    background-color: #ff8001;
    color: white;
    border: none;
    border-radius: 20px;
    display: flex;
    justify-content: center;
    align-items: center;
    gap: 5px;
    padding: 3px 9px; 
    font-size: 15px;
    cursor: pointer;
}
.students-table__download-btn:hover {
    opacity: 0.8;
    transform: scale(1.1);
    transition: all 0.2s ease-in-out;
}

/* template */

#students-table__body {
    width: 100%;
    display: flex;
    justify-content: start;
    align-items: center;
    flex-direction: column;
    gap: 10px;
}
.students-table__body-tr {
    width: 100%;
    display: flex;
    justify-content: space-between;
    align-items: center;
    background-color: #ccc;
    border-radius: 20px;
    padding: 10px 0;
}
.students-table__body-td {
    width: 14.28%; 
    display: flex;
    justify-content: center;
    align-items: center;
    font-family: "Atkinson Hyperlegible Mono", serif;
    font-optical-sizing: auto;
    font-weight: 400;
    font-style: normal;
    font-size: 13px;
}
.students-table__body-btn-group {
    display: flex;
    justify-content: center;
    align-items: center;
    gap: 10px;
}

.students-table__body-btn--edit {
    position: relative;
    width: 20px;
    height: 20px;
    padding: 10px;
    box-sizing: border-box;
    fill: black;
    background: inherit;
    border: none;
    cursor: pointer;
}
.students-table__body-btn--edit:hover {
    fill: green;
    transform: scale(1.1);
    transition: all 0.3s ease-in-out;
}
.students-table__body-btn--delete {
    position: relative;
    width: 20px;
    height: 20px;
    padding: 5px;
    box-sizing: border-box;
    fill: black;
    background: none;
    border: none;
    cursor: pointer;
}
.students-table__body-btn--delete:hover {
    fill: red;
    transform: scale(1.1);
    transition: all 0.3s ease-in-out;
}
.pen-svg {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
}
.basket-svg {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
}
.pen-svg, .basket-svg {
    pointer-events: none;
}

